DecoupledEditorBase.builtinPlugins = BuiltinPlugins;
DecoupledEditorBase.defaultConfig = DecoupledEditorDefaultConfig;

BalloonEditorBase.builtinPlugins = BuiltinPlugins;

ClassicEditorBase.builtinPlugins = BuiltinPlugins;
ClassicEditorBase.defaultConfig = ClassicEditorDefaultConfig;

InlineEditorBase.builtinPlugins = BuiltinPlugins;
InlineEditorBase.defaultConfig = InlineEditorDefaultConfig;

export default class CKEditor {
	static create( type, elRef, config ) {
		switch ( type ) {
			case 'decoupled':
				return DecoupledEditorBase.create( elRef, config );
			case 'balloon':
				return BalloonEditorBase.create( elRef, Object.assign( {}, BalloonEditorDefaultConfig, config ) );
			case 'balloon-block':
				return BalloonEditorBase.create( elRef, Object.assign( {}, BalloonBlockEditorDefaultConfig, config ) );
			case 'classic':
				return ClassicEditorBase.create( elRef, config );
			case 'inline':
				return InlineEditorBase.create( elRef, config );
			default:
				return Promise.reject( 'type must be one of [decoupled | balloon | balloon-block | classic | inline]' );
		}
	}
}

